svg.script:
let urlParams = new URLSearchParams(window.location.search);
if (urlParams.has('color')) {
  let fillColor = urlParams.get('color');
  let sc = new Option().style;
  let svgPath = document.querySelector("path");
  sc.color = fillColor;
  if (sc.color == fillColor) {
    svgPath.setAttribute("fill", fillColor)
  } else {
    if (/^[0-9A-F]{6}$/ig.test(fillColor)) {
      svgPath.setAttribute('fill', '#' + fillColor);
    }
  }
}
